INSERT INTO `note_page` (`page_id`, `space_id`, `user_id`, `parent_id`, `sort_number`, `icon`, `title`, `cover`, `content`, `collect`, `layout`, `font`, `size`, `readonly`, `password`, `share`, `template`, `deleted`, `create_time`, `update_time`) VALUES (1871964078761787393, 1871964078606598145, 1871328355301871618, 0, NULL, '😃', '欢迎！ThinKing', 'SystemDefaultFiles/images/cover/1.jpg', '{\"type\": \"doc\", \"content\": [{\"type\": \"heading\", \"attrs\": {\"id\": \"9402c6fa-0a6e-47b6-a2ef-f765fe480ba0\", \"level\": 1, \"data-toc-id\": \"9402c6fa-0a6e-47b6-a2ef-f765fe480ba0\"}, \"content\": [{\"text\": \"欢迎使用ThinKing🎈🦋\", \"type\": \"text\"}]}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"ThinKing 是一个创新的在线笔记应用，旨在\", \"type\": \"text\"}, {\"text\": \"帮助用户以更高效、更有组织的方式记录和管理他们的想法、笔记和任务。无论您是一名学生、专业人士还是自由职业者，Thi\", \"type\": \"text\", \"marks\": [{\"type\": \"highlight\", \"attrs\": {\"color\": \"#F8BBD0\"}}]}, {\"text\": \"nKing 都能为您提供一个灵活且功能强大的平台，以支持您的日常笔记需求。\", \"type\": \"text\"}]}, {\"type\": \"paragraph\"}, {\"type\": \"heading\", \"attrs\": {\"id\": \"46be5aa6-0eba-4661-a07c-2a71f7f5b3ec\", \"level\": 4, \"data-toc-id\": \"46be5aa6-0eba-4661-a07c-2a71f7f5b3ec\"}, \"content\": [{\"text\": \"高效记录，\", \"type\": \"text\"}, {\"text\": \"灵感不丢失\", \"type\": \"text\", \"marks\": [{\"type\": \"highlight\", \"attrs\": {\"color\": \"#F8BBD0\"}}]}]}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"在快节奏的现代生活中，灵感和想法常常在一瞬间涌现。ThinKing 提供了一个快速记录的环境，让用户能够随时随地捕捉这些宝贵的想法。无论是会议中的\", \"type\": \"text\"}, {\"text\": \"灵感、学习时的笔记，还是\", \"type\": \"text\", \"marks\": [{\"type\": \"bold\"}]}, {\"text\": \"日常生活中的待办事项，ThinKing 都能确保您的每一个想法都被妥善保存。\", \"type\": \"text\"}]}, {\"type\": \"paragraph\"}, {\"type\": \"heading\", \"attrs\": {\"id\": \"1a28ad93-fb58-415e-9dae-ba2d22820491\", \"level\": 4, \"data-toc-id\": \"1a28ad93-fb58-415e-9dae-ba2d22820491\"}, \"content\": [{\"text\": \"有组织的管理，\", \"type\": \"text\"}, {\"text\": \"轻松检索\", \"type\": \"text\", \"marks\": [{\"type\": \"highlight\", \"attrs\": {\"color\": \"#BBDEFB\"}}]}]}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"ThinKing 的核心优势在于其强大的组织和管理功能。用户可以通过创建不同的空间来对笔记进行分类，例如工作、学习、个人等。每个空间都可以包含无限数量的笔记，而每篇笔记都可以通过标签进行进一步的细分和标记。这种灵活的组织方式使得用户能够轻松地管理和检索他们的笔记，即使是在拥有大量笔记的情况下。\", \"type\": \"text\"}]}, {\"type\": \"paragraph\"}, {\"type\": \"heading\", \"attrs\": {\"id\": \"4e20f7c8-c8e1-4800-ad34-5625b4ace296\", \"level\": 4, \"data-toc-id\": \"4e20f7c8-c8e1-4800-ad34-5625b4ace296\"}, \"content\": [{\"text\": \"多功能笔记，\", \"type\": \"text\"}, {\"text\": \"满足多样化需求\", \"type\": \"text\", \"marks\": [{\"type\": \"highlight\", \"attrs\": {\"color\": \"#FFAB91\"}}]}]}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"ThinKing 支持多种笔记类型，包括但不限于文本、图片、链接和代码片段。用户可以根据自己的需求选择最适合的笔记格式。例如，学生可以使用文本和图片笔记来记录课堂内容，开发者可以使用代码片段笔记来保存重要的代码示例。这种多功能性确保了 ThinKing 能够满足不同用户群体的多样化需求。\", \"type\": \"text\"}]}, {\"type\": \"paragraph\"}, {\"type\": \"paragraph\"}, {\"type\": \"paragraph\"}, {\"type\": \"heading\", \"attrs\": {\"id\": \"8deccccc-8e14-4640-9ddb-041661409f62\", \"level\": 1, \"data-toc-id\": \"8deccccc-8e14-4640-9ddb-041661409f62\"}, \"content\": [{\"text\": \"支持以下格式\", \"type\": \"text\"}]}, {\"type\": \"heading\", \"attrs\": {\"id\": \"1881c0db-1fdf-491e-8f39-df38c10ef958\", \"level\": 3, \"data-toc-id\": \"1881c0db-1fdf-491e-8f39-df38c10ef958\"}, \"content\": [{\"text\": \"标题\", \"type\": \"text\"}]}, {\"type\": \"horizontalRule\"}, {\"type\": \"heading\", \"attrs\": {\"id\": \"89e823f8-c85c-4dac-b814-4ea8d398c0f0\", \"level\": 1, \"data-toc-id\": \"89e823f8-c85c-4dac-b814-4ea8d398c0f0\"}, \"content\": [{\"text\": \"标题一\", \"type\": \"text\"}]}, {\"type\": \"heading\", \"attrs\": {\"id\": \"47859c05-b893-468e-b3c7-d2a24d0d8a84\", \"level\": 2, \"data-toc-id\": \"47859c05-b893-468e-b3c7-d2a24d0d8a84\"}, \"content\": [{\"text\": \"标题二\", \"type\": \"text\"}]}, {\"type\": \"heading\", \"attrs\": {\"id\": \"685af503-3bc1-4926-9bb1-7d32122c2396\", \"level\": 3, \"data-toc-id\": \"685af503-3bc1-4926-9bb1-7d321