INSERT INTO `note_page` (`page_id`, `space_id`, `user_id`, `parent_id`, `sort_number`, `icon`, `title`, `cover`, `content`, `collect`, `layout`, `font`, `size`, `readonly`, `password`, `share`, `template`, `deleted`, `create_time`, `update_time`) VALUES (1871979307289755650, 1865162594686513153, 1859329812227633153, 0, NULL, '🗽', '名人名言', 'SystemDefaultFiles/images/cover/26.jpg', '{\"type\": \"doc\", \"content\": [{\"type\": \"blockquote\", \"content\": [{\"type\": \"heading\", \"attrs\": {\"id\": \"3651fe86-98ab-44cf-b9c9-375b30dc41f3\", \"level\": 6, \"data-toc-id\": \"3651fe86-98ab-44cf-b9c9-375b30dc41f3\"}, \"content\": [{\"text\": \"你的时间有限，不要为别人而活。\", \"type\": \"text\"}]}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"------史蒂夫·乔布斯（美国）\", \"type\": \"text\"}]}]}, {\"type\": \"bulletList\", \"content\": [{\"type\": \"listItem\", \"content\": [{\"type\": \"paragraph\", \"content\": [{\"text\": \"感悟\", \"type\": \"text\", \"marks\": [{\"type\": \"bold\"}]}, {\"text\": \"：\", \"type\": \"text\"}, {\"text\": \"生命是宝贵的，我们应当珍惜并充分利用自己的时间，去追求自己真正热爱和相信的事情。不要被他人的期望或社会的规范所束缚，活出真实的自我，这样我们才能在有限的时间内实现个人的价值和梦想。\", \"type\": \"text\", \"marks\": [{\"type\": \"textStyle\", \"attrs\": {\"color\": \"rgb(6, 6, 7)\"}}]}]}]}]}, {\"type\": \"paragraph\"}, {\"type\": \"blockquote\", \"content\": [{\"type\": \"heading\", \"attrs\": {\"id\": \"bd97974e-b656-4859-99be-ef99b4247c1d\", \"level\": 6, \"data-toc-id\": \"bd97974e-b656-4859-99be-ef99b4247c1d\"}, \"content\": [{\"text\": \"黑夜无论怎样悠长，白昼总会到来。\", \"type\": \"text\", \"marks\": [{\"type\": \"textStyle\", \"attrs\": {\"color\": \"rgb(6, 6, 7)\"}}]}]}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"------\", \"type\": \"text\"}, {\"text\": \"莎士比亚（英国）\", \"type\": \"text\", \"marks\": [{\"type\": \"bold\"}]}]}]}, {\"type\": \"bulletList\", \"content\": [{\"type\": \"listItem\", \"content\": [{\"type\": \"paragraph\", \"content\": [{\"text\": \"感悟\", \"type\": \"text\", \"marks\": [{\"type\": \"bold\"}]}, {\"text\": \"：\", \"type\": \"text\"}, {\"text\": \"无论当前的困境有多么艰难，总有结束的时候。正如黑夜之后必然是黎明，我们也应该保持耐心和坚韧，相信困难终将过去，新的开始即将到来。\", \"type\": \"text\", \"marks\": [{\"type\": \"textStyle\", \"attrs\": {\"color\": \"rgb(6, 6, 7)\"}}]}]}]}]}, {\"type\": \"paragraph\"}, {\"type\": \"blockquote\", \"content\": [{\"type\": \"heading\", \"attrs\": {\"id\": \"a3efc8a6-a08e-4281-9700-58dce359d563\", \"level\": 6, \"data-toc-id\": \"a3efc8a6-a08e-4281-9700-58dce359d563\"}, \"content\": [{\"text\": \"一往无前，先干了再说\", \"type\": \"text\", \"marks\": [{\"type\": \"textStyle\", \"attrs\": {\"color\": \"rgb(6, 6, 7)\"}}]}]}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"------\", \"type\": \"text\"}, {\"text\": \"雷军\", \"type\": \"text\", \"marks\": [{\"type\": \"bold\"}]}]}]}, {\"type\": \"bulletList\", \"content\": [{\"type\": \"listItem\", \"content\": [{\"type\": \"paragraph\", \"content\": [{\"text\": \"感悟\", \"type\": \"text\", \"marks\": [{\"type\": \"bold\"}]}, {\"text\": \"：\", \"type\": \"text\"}, {\"text\": \"面对挑战时不要犹豫不决，而是要勇敢地迈出第一步。有时候，行动本身就是解决问题的开始，只有真正去做了，才能发现问题、解决问题，并最终实现目标。\", \"type\": \"text\", \"marks\": [{\"type\": \"textStyle\", \"attrs\": {\"color\": \"rgb(6, 6, 7)\"}}]}]}]}]}, {\"type\": \"paragraph\"}, {\"type\": \"blockquote\", \"content\": [{\"type\": \"heading\", \"attrs\": {\"id\": \"59b1ee8e-e9f7-474b-8800-b84cfd4b5807\", \"level\": 6, \"data-toc-id\": \"59b1ee8e-e9f7-474b-8800-b84cfd4b5807\"}, \"content\": [{\"text\": \"彪悍的人生不需要解释。\", \"type\": \"text\", \"marks\": [{\"type\": \"textStyle\", \"attrs\": {\"color\": \"rgb(6, 6, 7)\"}}]}]}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"------\", \"type\": \"text\"}, {\"text\": \"罗永浩\", \"type\": \"text\", \"marks\": [{\"type\": \"bold\"}]}]}]}, {\"type\": \"bulletList\", \"content\": [{\"type\": \"listItem\", \"content\": [{\"type\": \"paragraph\", \"content\": [{\"text\": \"感悟\", \"type\": \"text\", \"marks\": [{\"type\": \"bold\"}]}, {\"text\": \"：\", \"type\": \"text\"}, {\"text\": \"真正强大的人不需要向他人证明自己的价值。\", \"type\": \"text\", \"marks\": [{\"type\": \"textStyle\", \"attrs\": {\"color\": \"rgb(6, 6, 7)\"}}]}]}]}]}]}', 0, 'default', 'default', '16', '0', NULL, 0, 0, 0, '2024-12-26 02:00:21', '2024-12-26 02:15:22');
